import { 
   FETCH_DATA_START, 
   FETCH_DATA_SUCCESS, 
   FETCH_DATA_FAIL, 
   POST_DATA_START, 
   POST_DATA_SUCCESS, 
   POST_DATA_FAIL,
   REGISTER_USER_START,
   REGISTER_USER_SUCCESS,
   REGISTER_USER_FAIL
} from '../actions';

const initialState = {
   isFetching: false,
   isLoggingIn: false,
   loggedIn: false,
   isRegistering: false,
   isRegistered: false,
   error: null,
   recipes: []
}

function reducer(state = initialState, action) {
   switch(action.type) {
      case FETCH_DATA_START:
         return {
            ...state,
            isFetching: true,
            error: null,
            recipes: []
         }
      case FETCH_DATA_SUCCESS:
         return {
            ...state,
            isFetching: false,
            error: null,
            recipes: action.payload
         }
      case FETCH_DATA_FAIL:
         return {
            ...state,
            isFetching: false,
            error: action.payload,
            recipes: []
         }
      default: 
         return state;
   }
}

export default reducer;
