import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { fetchUserRecipes, fetchProfile } from '../../actions';
import {axiosWithAuth} from '../../protected/axiosWithAuth';
import Header from '../header/Header';

function RecipeList(props) {
    const { fetchUserRecipes, recipes, fetchProfile, userProfile } = props;

    async function getProfile() {
        // Wait for profile to be fetched.
        return await axiosWithAuth().get('http://localhost:5000/api/users/profile');

    }

    const [user,setUser] = useState({});

    // Fetch recipes on load
    useEffect(() => {
        // Then get recipes after user info has been fetched.
        console.log('calling...')
        // getProfile().then(res => fetchUserRecipes(res.data.id));
        fetchProfile()
        setUser(userProfile)
    }, []);

    useEffect(() => {
        console.log(user);
        fetchUserRecipes(user.id);
    }, [setUser]);

    // console.log('recipes:', recipes);

    return (
        <>
            <Header />
            <div className="recipe-list">
                <div className="container">
                    <h1>Your Recipes</h1>
                    {recipes.length === 0 ? <p>You have no recipes yet...</p> :
                    recipes.map(recipe => (
                        <h3 key={recipe.id}>{recipe.title}</h3>
                    ))}
                </div>
            </div>
        </>
    )
}

function mapStateToProps(state) {
    return {
        recipes: state.recipes,
        userProfile: state.userProfile
    }
}

// export default RecipeList;
export default connect(
    mapStateToProps, { fetchUserRecipes, fetchProfile }
)(RecipeList);
