import React, { useState } from 'react'
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import { connect } from 'react-redux';
import { loginUser } from '../../actions';

// Components
import Header from '../header/Header';

function Login(props) {

   const { loginUser, loggedIn, isLoggingIn } = props;

   const [userInput, setUserInput] = useState({
      username: '',
      password: ''
   });

   // handle change to inputs
   const handleChange = (e) => {
      setUserInput({
         ...userInput,
         [e.target.name]: e.target.value
      })
   }

   const submitForm = (e) => {
      e.preventDefault();
      loginUser(userInput);
      setUserInput({
         username: '',
         password: ''
      });
      console.log('logged in:', loggedIn);
      // if (loggedIn) {
      //    console.log('logged in:', loggedIn);
      //    // props.history.push(`/user/${}/recipes`);
      // } else {
      //    console.log('logged in:', loggedIn);
      // }
   }

   const redirectToDashboard = () => {
      props.history.push('/');
   }

   // Redirect to register form
   const redirect = () => {
      props.history.push('/register');
   }

   return (
      <>
         <Header />
         <div className="login">
            <div className="container">
               <div className="login-wrapper">
                  <h1>Log in</h1>
                  <Form onSubmit={submitForm}>
                     <FormGroup>
                        <Label for="username">Username:</Label>
                        <Input
                           type="username"
                           name="username"
                           id="username"
                           placeholder="Enter your username"
                           value={userInput.username}
                           onChange={handleChange}
                        />
                     </FormGroup>
                     <FormGroup>
                        <Label for="password">Password:</Label>
                        <Input
                           type="password"
                           name="password"
                           id="password"
                           placeholder="Enter your password"
                           value={userInput.password}
                           onChange={handleChange}
                        />
                     </FormGroup>
                     <Button color="primary">Login</Button>
                     <Button color="secondary" onClick={redirect}>Register</Button>
                  </Form>
                  {isLoggingIn && (<h1>Loggin in</h1>)}
                  {(!isLoggingIn && loggedIn) ? redirectToDashboard() : (<p>Invalid creds</p>)}
               </div>
            </div>
         </div>
      </>
   )
}

const mapStateToProps = (state) => {
   return {
      isLoggingIn: state.isLoggingIn,
      loggedIn: state.loggedIn
   }
}

export default connect(mapStateToProps, { loginUser })(Login)
