import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { fetchUserRecipes, fetchUserProfile } from '../../actions';

import Header from '../header/Header';

function RecipeList(props) {
    const { fetchUserRecipes, currentUser, fetchUserProfile, profileFetched } = props;

    const [recipes, setRecipes] = useState([]);

    // Get user id from reducer state
    console.log('user:', currentUser);
    const userId = currentUser.id;

    // Fetch recipes on load
    useEffect(() => {
        console.log('fetched profile', profileFetched);
        const data = fetchUserRecipes(userId);
        console.log('data', data);
        setRecipes(fetchUserRecipes(userId));

    }, [profileFetched]);

    console.log('RECIPESS:', recipes);
    return (
        <>
            <Header />
            <div className="recipe-list">
                <div className="container">
                    <h1>Your Recipes</h1>
                </div>
            </div>
        </>
    )
}

function mapStateToProps(state) {
    return {
        recipes: state.recipes,
        currentUser: state.currentUser,
        profileFetched: state.profileFetched
    }
}

// export default RecipeList;
export default connect(
    mapStateToProps, { fetchUserRecipes, fetchUserProfile }
)(RecipeList);
