import React from 'react';
import { withRouter, Redirect } from 'react-router-dom';
import { Card, CardTitle, CardText, CardBody, CardLink, Button} from 'reactstrap';

function RecipeCard(props) {
	const { recipe } = props;

	function redirectToDetails(id) {
		// props.history.push(`/recipe/${id}/details`);
		return <Redirect to={`/recipe/${id}/details`} />
	}

	return (
		<>
			<Card body className="recipe-card">
				<CardBody className="card-titles">
					<CardTitle>{recipe.title}</CardTitle>
					<CardText>{recipe.description}</CardText>
				</CardBody>
				<CardBody className="card-links">
					<Button color="warning" onClick={redirectToDetails(recipe.id)}>Details</Button>
					<Button color="warning" outline>Edit</Button>
				</CardBody>
			</Card>
		</>
	)
}

export default withRouter(RecipeCard);
