import React from 'react';
import { Redirect } from 'react-router-dom';
import { Card, CardTitle, CardText, CardBody, CardLink, Button} from 'reactstrap';

function RecipeCard(props) {
	const { recipe } = props;

	const redirectToDetails = (id) => {
		<Redirect to={`/recipe${id}/details`} />
	}

	return (
		<>
			<Card body className="recipe-card">
				<CardBody className="card-titles">
					<CardTitle>{recipe.title}</CardTitle>
					<CardText>{recipe.description}</CardText>
				</CardBody>
				<CardBody className="card-links">
					<CardLink>Details</CardLink>
					<CardLink>Edit</CardLink>
				</CardBody>
			</Card>
		</>
	)
}

export default RecipeCard;
