import {
	FETCH_INGREDIENTS_START,
	FETCH_INGREDIENTS_SUCCESS,
	FETCH_INGREDIENTS_FAIL
} from './types';

const initialState = {
	isFetching: false,
	error: null,
	ingredients: [],
}

function ingredientsReducer(state = initialState, action) {
	switch (action.type) {
		case FETCH_INGREDIENTS_START: {
			return {
				...state,
				isFetching: true,
				error: null
			}
		}
		default:
			return state;
	}
}

export default ingredientsReducer;
